import React, { useState } from 'react'
import './event.css';

const Event = (props) => {

  const [toggleEvent, setToggleEvent] = useState(false);

  const EventPlus = () => (
    <>
      <div className='website__events-resultados'>
        <h2>Resultados:</h2><h1></h1>
          {props.evento.resultados.map((result, index) => (
            <div className='website__events-resultados-person'>
              <h3>{result.nombre}</h3>
              <p>{result.categoria}</p>
              {result.pruebas.map((prueba, index2) => (
                  <p>-{prueba.nombre}: {prueba.tiempo}</p>
              ))}
            </div>
          ))}
      </div>
    </>
  )

  return (
    <div>
        <div className='website__eventos-contentiner-evento' onMouseEnter={() => {setToggleEvent(true)}} onMouseLeave={() => {setToggleEvent(false)}}>
            <h2>{props.evento.nombre}</h2>
            <p><strong>Fecha:</strong> {props.evento.fecha}</p>
            <p><strong>Lugar:</strong> {props.evento.lugar}</p>
            {toggleEvent && (
                  <EventPlus />
            )}
        </div>
    </div>
  )
}

export default Event
